import React,{useState} from 'react';

function App() {
  const[formData, setformData] = useState( { firstName:"", lastName: "", email:"", country:"", street:"", city: "", state: "" , zip: "" } )
  console.log(formData);

  function handleForm(event){
    setformData(prevformData => {
      return{
        ...prevformData,
        [event.target.name] : event.target.value
      }
    })
  }
  function handleSubmit(){
    console.log(formData);
  }
  return (
    <div className="flex flex-col justify-center items-center mx-auto m-10">
      <form onSubmit={handleSubmit}>
      <label htmlFor='firstName'>Enter your First Name</label>
      <br/>
      <input type="text" placeholder="Sidra" id="firstName" onChange={handleForm} name="firstName" value={formData.firstName}></input>
      <br/>  
      <label htmlFor='lastName'>Enter your Last Name</label>
      <br/>
      <input type="text" placeholder="Fatima" id="lastName" onChange={handleForm} name="lastName" value={formData.lastName}></input>
      <br/>
      <label htmlFor='email'>Enter your Email ID</label>
      <br/>
      <input type="text" placeholder="sf@gmail.com" id="email" onChange={handleForm} name="email" value={formData.email}></input>
      <br/>
      <label htmlFor='country'>Enter your Country</label>
      <br/>
      <input type="text" placeholder="India" id="country" onChange={handleForm} name="country" value={formData.country}></input>
      <br/>
      <label htmlFor='street'>Enter your Street Address</label>
      <br/>
      <input type="text" placeholder="Hinjawadi" id="street" onChange={handleForm} name="street" value={formData.street}></input>
      <br/>
      <label htmlFor='city'>Enter your City</label>
      <br/>
      <input type="text" placeholder="Pune" id="city" onChange={handleForm} name="city" value={formData.city}></input>
      <br/>
      <label htmlFor='state'>Enter your state</label>
      <br/>
      <input type="text" placeholder="Maharashtra" id="state" onChange={handleForm} name="state" value={formData.state}></input>
      <br/>
      <label htmlFor='zip'>Enter your First Name</label>
      <br/>
      <input type="text" placeholder="411001" id="zip" onChange={handleForm} name="zip" value={formData.zip}></input>
      <br/>
      <button>Submit</button>
      </form>


    </div>
  );
}

export default App;
