import React,{useState} from 'react';

function App() {
  const[formData, setformData] = useState( { FirstName:"", LastName: "", Email:"", Country:"", Street:"", City: "", State: "" , Zip: "" } )
  console.log(formData);
  function handleForm(event){
    setformData(prevformData => {
      return{
        ...prevformData,
        [event.target.name] : event.target.value
      }
    })
  }
  return (
    <div className="flex flex-col justify-center items-center mx-auto">
      <form>
        <label htmlFor="FirstName">Enter your First Name</label>
        <input type="text" onChange={handleForm} placeholder="Sidra" id="FirstName" name="FirstName" value={formData.FirstName}/>
      </form>

    </div>
  );
}

export default App;
