import React,{useState} from 'react';

function App() {
  const[formData, setformData] = useState( { FirstName:"", LastName: "", Email:"", Country:"", Street:"", City: "", State: "" , Zip: "" } )
  console.log(formData);

  function handleForm(event){
    setformData(prevformData => {
      return{
        ...prevformData,
        [event.target.name] : event.target.value
      }
    })
  }
  return (
    <div className="flex flex-col justify-center items-center mx-auto">
      <form>
        <label htmlFor="FirstName">Enter your First Name</label>
        <br/>
        <input type="text" onChange={handleForm} placeholder="Sidra" id="FirstName" name="FirstName" value={formData.FirstName}/>
        <label htmlFor="FirstName">Enter your First Name</label>
        <br/>
        <input type="text" onChange={handleForm} placeholder="Fatima" id="FirstName" name="FirstName" value={formData.FirstName}/>
        <label htmlFor="FirstName">Enter your First Name</label>
        <br/>
        <input type="text" onChange={handleForm} placeholder="fatimas@gmail.com" id="FirstName" name="FirstName" value={formData.FirstName}/>
        <label htmlFor="FirstName">Enter your First Name</label>
        <br/>
        <input type="text" onChange={handleForm} placeholder="India" id="FirstName" name="FirstName" value={formData.FirstName}/>
        <label htmlFor="FirstName">Enter your First Name</label>
        <br/>
        <input type="text" onChange={handleForm} placeholder="Pune" id="FirstName" name="FirstName" value={formData.FirstName}/>
        <label htmlFor="FirstName">Enter your First Name</label>
        <br/>
        <input type="text" onChange={handleForm} placeholder="Hinjawadi" id="FirstName" name="FirstName" value={formData.FirstName}/>
        <label htmlFor="FirstName">Enter your First Name</label>
        <br/>
        <input type="text" onChange={handleForm} placeholder="Maharashtra" id="FirstName" name="FirstName" value={formData.FirstName}/>
        <label htmlFor="FirstName">Enter your First Name</label>
        <br/>
        <input type="text" onChange={handleForm} placeholder="411001" id="FirstName" name="FirstName" value={formData.FirstName}/>

      </form>

    </div>
  );
}

export default App;
