import React,{useState} from 'react';

function App() {
  const[formData, setformData] = useState( { firstName:"", lastName: "", email:"", country:"", street:"", city: "", state: "" , zip: "" } )
  console.log(formData);

  function handleForm(event){
    setformData(prevformData => {
      return{
        ...prevformData,
        [event.target.name] : event.target.value
      }
    })
  }
  return (
    <div className="flex flex-col justify-center items-center mx-auto">
      <form>
      <label htmlFor='firstName'>Enter your First Name</label>
      <br/>
      <input type="text" placeholder="Sidra" id="firstName" onChange={handleForm} name="firstName" value={formData.firstName}></input>
      <br/>  
      <label htmlFor='lastName'>Enter your Last Name</label>
      <br/>
      <input type="text" placeholder="Fatima" id="lastName" onChange={handleForm} name="lastName" value={formData.lastName}></input>
      <br/>    
      </form>

    </div>
  );
}

export default App;
