import React from 'react';
import {useState} from 'react';

function App() {

  const [formData, setformData] = useState( { firstName: "" , lastName: "", email: "", age: "" } );

  function handleChange(event){
    setformData(prevformData => {
      return{
        ...prevformData,
        [event.target.name] : event.target.value
      }
    });
  }

  return(
    <div>
    <form> 
      <input type="text" placeholder="FirstName" onChange={handleChange} name="firstName"/>
      <br/>
      <input type="text" placeholder="LastName" onChange={handleChange} name="lastName"/>
      <br/>
      <input type="text" placeholder="Email" onChange={handleChange} name="email"/>
      <br/>
      <input type="text" placeholder="Age" onChange={handleChange} name="age"/>
    </form>
    </div>
  );
}

export default App;
















