import React from 'react';
import {useState} from 'react';

function App() {

  const[firstName, setFirstName] = useState("");
  const[lastName, setLastName] = useState("");

  console.log(firstName);
  console.log(lastName);

  function handleFChange(event){
    console.log("Inside handleFChange")
    console.log(event.target.value)
    setFirstName(event.target.value);
  }
  function handleLChange(event){
    console.log("Inside handleLChange")
    console.log(event.target.value)
    setLastName(event.target.value);
  }

  return (
    <div className='App'>
      <h1> FORM </h1>
    <form> 
      <input type="text" placeholder="FirstName" onChange={handleFChange}/>
      <br/>
      <input type="text" placeholder="LastName" onChange={handleLChange}/>
    </form>
    </div>
  );
}

export default App;
